lightbox.option({
    'resizeImage': false,
    'fadeDuration': 500
  });

  function sendMessage() {
    const messageInput = document.getElementById("message-input");
    const chatMessages = document.getElementById("chat-messages");
  
    const userInput = messageInput.value;
    chatMessages.innerHTML += `<div class="user-message">${userInput}</div>`;
    messageInput.value = "";
  
    // Simulate a bot response (replace with your own logic)
    const botResponse = "Hello, how can I help you today?";
    chatMessages.innerHTML += `<div class="bot-message">${botResponse}</div>`;
  }
  const sendButton = document.getElementById("send-button");
sendButton.addEventListener("click", sendMessage);
